INSERT INTO `DBPRODUCT` (`ID`, `NAME`, `DEVELOPER_NAME`, `EMAIL`, `CLASS`, `TYPE`, `PRICE`, `DESCRIBE`, `UPLOAD_DATE`, `ACCEPT_RELEASE`, `ACCEPT_DATE`, `BUY_TIMES`, `DOWNLOAD_TIMES`) VALUES
('GA1524453979', 'Boom Karts', 'Nguyen Binh Thang', 'nguyenbinhthang@gmail.com', 'APPLICATION', 'GAME', 0, 'Enter the world of Boom Karts and battle it out online against your friends and players from all around the world! Get behind the wheel and race around a variety of tracks in this arcade karting game. The races are in real time so you will always be right in the frantic action!', '2018-04-23', 1, '2018-04-26', 50, 77),
('GA1574382441', 'Table Top Racing', 'Ha Ngoc My', 'hangocmy@gmail.com', 'APPLICATION', 'GAME', 0, 'Get behind the wheel of 16 ultra-cool, miniaturised racing cars and engage in combat-racing like no other. Master over 30 table-top race tracks and over 180 events in a bid for glory, then take the battle Online and compete with the best in the world. Crush your enemies with 8 cunning Power-ups and 6 unique Weapon Wheels and blast your way to victory!', '2019-11-22', 0, '2019-11-24', 46, 69),
('GA1584083033', 'Farmscapes', 'Ly Huong Yen', 'lyhuongyen@gmail.com', 'APPLICATION', 'GAME', 0, 'Play colorful puzzles to harvest your way through the exciting story of a former city girl transforming along with her farm. There are plenty of challenges ahead, but don’t you worry — charismatic handyman Philip has you and Mary covered, with tools and jokes aplenty!', '2020-03-13', 1, '2020-03-15', 25, 45),
('GA1613116859', 'Tropic Trouble', 'Nguyen Chi Cuong', 'nguyenchicuong@gmail.com', 'APPLICATION', 'GAME', 0, 'Welcome to Tropic Trouble! While sailing from San Francisco to Hawaii, your family’s ship was hit by a storm and wrecked upon a mysterious, uncharted island! Now, they need your help! Help build your family a home, find food, and turn this mysterious island into your own tropical escape!', '2021-02-12', 1, '2021-02-14', 47, 76),
('GA1562744407', 'Sonic Dash', 'Nguyen Binh Thang', 'nguyenbinhthang@gmail.com', 'APPLICATION', 'GAME', 0, 'Run fast with Sonic the Hedgehog in his first endless running game from SEGA! How far can you run with this fast and cool hedgehog? Use Sonic’s super running powers to run farther, faster than anyone else!', '2019-07-10', 1, '2019-07-11', 32, 69),
('GA1523571577', 'Talking Tom', 'Ha Ngoc My', 'hangocmy@gmail.com', 'APPLICATION', 'GAME', 164, 'Talking Tom Gold Run takes you on a race to catch The Rakoonz - a bunch of pesky raccoon bandits on a mission to commit daylight robbery! Get ready to run, dash and race through the streets in a fun-filled chase.', '2018-04-13', 0, '2018-04-13', 28, 52),
('GA1535975102', 'Dragon City', 'Ly Huong Yen', 'lyhuongyen@gmail.com', 'APPLICATION', 'GAME', 235, 'Ready to master this dragon game and collect and breed tons of adorable fire-breathing dragons? Train them to your will, grow your collection, build your city, and prove your might to be the top Dragon Master in the world!', '2018-09-03', 1, '2018-09-04', 30, 55),
('GA1590697208', 'Idle Museum Tycoon', 'Nguyen Chi Cuong', 'nguyenchicuong@gmail.com', 'APPLICATION', 'GAME', 76, 'Culture, and History is your business! You need to administer exhibitions of all kinds to attract visitors to see and contemplate the most incredible art and history collections of all time!', '2020-05-28', 0, '2020-05-29', 32, 57),
('GA1582398861', 'Age of Magic', 'Nguyen Binh Thang', 'nguyenbinhthang@gmail.com', 'APPLICATION', 'GAME', 133, 'Join the epic battle of the True Mages, who fight for the destiny of a dying world. Assemble and level up legendary generals and take them into lightning-fast battles in stunning arenas. Playful Ghosts, Elves, Demons, Rage Raccoons, Dragons, RaArchnes, Swamp Witches – dozens of characters await you!', '2020-02-22', 1, '2020-02-24', 47, 71),
('GA1543488220', 'Juggernaut Wars', 'Ha Ngoc My', 'hangocmy@gmail.com', 'APPLICATION', 'GAME', 160, 'Welcome to the world of Juggernaut Wars — a massive fantasy RPG! Be a legendary player! A world where swords, magic and bravery will fight extraterrestrial demons and hordes of minions and fierce battles without end! ', '2018-11-29', 0, '2018-12-01', 47, 79),
